import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [tweetContent, setTweetContent] = useState('');
  const [tweets, setTweets] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchTweets();
  }, []);

  const fetchTweets = async () => {
    try {
      const response = await fetch('http://localhost:8080/tweets');

      if (!response.ok) {
        throw new Error(`Failed to fetch tweets: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setTweets(data);
    } catch (error) {
      console.error('Error fetching tweets:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/create_tweet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: tweetContent }),
        mode: 'cors',
      });

      if (!response.ok) {
        throw new Error(`An error occurred while creating the tweet: ${response.status} ${response.statusText}`);
      }

      const newTweet = await response.json();
      setTweets([...tweets, newTweet]);
      setTweetContent('');
      setMessage('Tweet created successfully');
    } catch (error) {
      console.error('Error:', error.message);
      setMessage(error.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:8080/delete_tweet/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'cors',
      });

      if (!response.ok) {
        throw new Error(`An error occurred while deleting the tweet: ${response.status} ${response.statusText}`);
      }

      setTweets(tweets.filter((tweet) => tweet.id !== id));
      setMessage('Tweet deleted successfully');
    } catch (error) {
      console.error('Error:', error.message);
      setMessage(error.message);
    }
  };

  const handleEdit = async (id, newContent) => {
    try {
      const response = await fetch(`http://localhost:8080/edit_tweet/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ content: newContent }),
        mode: 'cors',
      });

      if (!response.ok) {
        throw new Error(`An error occurred while editing the tweet: ${response.status} ${response.statusText}`);
      }

      const editedTweet = await response.json();
      setTweets((prevTweets) =>
        prevTweets.map((tweet) => (tweet.id === editedTweet.id ? editedTweet : tweet))
      );
      setMessage('Tweet edited successfully');
    } catch (error) {
      console.error('Error:', error.message);
      setMessage(error.message);
    }
  };

  return (
    <div className="App">
      <h1>Twitter Clone</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tweetContent">Compose a Tweet:</label>
        <br />
        <textarea
          id="tweetContent"
          name="tweetContent"
          rows="4"
          cols="50"
          required
          value={tweetContent}
          onChange={(e) => setTweetContent(e.target.value)}
        ></textarea>
        <br />
        <br />
        <input type="submit" value="Tweet" />
      </form>

      {message && <div className="success-message">{message}</div>}

      <div className="tweet-list">
        <h2>Tweets</h2>
        {tweets.map((tweet) => (
          <div key={tweet.id} className="tweet">
            <p>{tweet.content}</p>
            <div>
              <button onClick={() => handleEdit(tweet.id, prompt('Edit your tweet:', tweet.content))}>
                Edit
              </button>
              <button onClick={() => handleDelete(tweet.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
