import React, { useState } from 'react';
import './App.css';

function App() {
  const [tweetContent, setTweetContent] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/create_tweet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `tweetContent=${encodeURIComponent(tweetContent)}`,
        mode: 'cors',
      });

      if (!response.ok) {
        throw new Error('An error occurred while creating the tweet.');
      }

      const data = await response.text();
      alert(data);
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while creating the tweet.');
    }
  };

  return (
    <div className="App">
      <h1>Create a Tweet</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tweetContent">Tweet Content:</label>
        <br />
        <textarea
          id="tweetContent"
          name="tweetContent"
          rows="4"
          cols="50"
          required
          value={tweetContent}
          onChange={(e) => setTweetContent(e.target.value)}
        ></textarea>
        <br />
        <br />
        <input type="submit" value="Create Tweet" />
      </form>
    </div>
  );
}

export default App;
